SET NOCOUNT OFF

DELETE	dst
FROM	[dbo].[refBisacSubjects] dst
	LEFT JOIN [#refBisacSubjects] src ON dst.[BISACCode] = src.[BISACCode]
WHERE	src.[BISACCode] IS NULL;

UPDATE	dst
SET	[Hierarchy] = src.[Hierarchy]
,	[Description] = src.[Description]
FROM	[dbo].[refBisacSubjects] dst
JOIN	[#refBisacSubjects] src ON dst.[BISACCode] = src.[BISACCode]
WHERE	src.[Hierarchy] <> dst.[Hierarchy]
OR	(src.[Description] IS NULL AND dst.[Description] IS NOT NULL) OR (src.[Description] IS NOT NULL AND dst.[Description] IS NULL) OR src.[Description] <> dst.[Description];

INSERT	[dbo].[refBisacSubjects] (
	[BISACCode], [Hierarchy], [Description]
)
SELECT	src.[BISACCode], src.[Hierarchy], src.[Description]
FROM	[#refBisacSubjects] src
	LEFT JOIN [dbo].[refBisacSubjects] dst ON dst.[BISACCode] = src.[BISACCode]
WHERE	dst.[BISACCode] IS NULL;
