DELETE	dst
FROM	[dbo].[refLayoutModules] dst
	LEFT JOIN [#refLayoutModules] src ON dst.[LayoutModuleId] = src.[LayoutModuleId]
WHERE	src.[LayoutModuleId] IS NULL;

UPDATE	dst
SET	[Description] = src.[Description]
,	[VisibleToCart] = src.[VisibleToCart]
FROM	[dbo].[refLayoutModules] dst
JOIN	[#refLayoutModules] src ON dst.[LayoutModuleId] = src.[LayoutModuleId]
WHERE	(src.[Description] IS NULL AND dst.[Description] IS NOT NULL) OR (src.[Description] IS NOT NULL AND dst.[Description] IS NULL) OR src.[Description] <> dst.[Description]
OR	src.[VisibleToCart] <> dst.[VisibleToCart];

INSERT	[dbo].[refLayoutModules] (
	[LayoutModuleId], [Description], [VisibleToCart]
)
SELECT	src.[LayoutModuleId], src.[Description], src.[VisibleToCart]
FROM	[#refLayoutModules] src
	LEFT JOIN [dbo].[refLayoutModules] dst ON dst.[LayoutModuleId] = src.[LayoutModuleId]
WHERE	dst.[LayoutModuleId] IS NULL;
